const PCs = ({pcs}) => {

  const displayCards = pcs.forEach(char => {
    return (
      <PcCard
        charName={char.charName}
        playerName={char.playerName}
        concept={char.concept} 
        image={char.image}
      />
    )
  });

  return (
    <section>
      {displayCards}
    </section>
  )
}

export default PCs
