import { eventWrapper } from "@testing-library/user-event/dist/utils";
import React, { Component } from "react";
import './Form.css'

class Form extends Component {
  constructor() {
    super()
    this.state = {
      charName: '',
      concept: '',
      playerName: '',
      image: '',
      brawn: 1,
      agility: 1,
      intellegence: 1,
      wit: 1,
      charm: 1,
      presence: 1,
      maxHealth: 0,
      currentHealth: 0,
      healthy: true,
      injured: false,
      unconscious: false,
      key: Date.now()
    }
  }

  handleChange = (event) => {
    event.preventDefault()
    this.setState( {[event.target.name]: event.target.value})
  }

  submitPC = (event) => {
    event.preventDefault()
    this.props.addPC(this.state)
  }

  stepUp = (event) => {
    event.preventDefault()
    this.setState( {[event.target.name]: event.target.value ++ })
  }

  stepDown = (event) => {
    event.preventDefault()
    this.setState( {[this.state[event.target.name]]: event.target -= 1})
  }

