import { eventWrapper } from "@testing-library/user-event/dist/utils";
import React, { Component } from "react";
import './Form.css'

class Form extends Component {
  constructor() {
    super()
    this.state = {
      charName: '',
      concept: '',
      playerName: '',
      image: '',
      attributes: {
        brawn: 1,
        agility: 1,
        intellegence: 1,
        wit: 1,
        charm: 1,
        presence: 1
      },
      vitals: {
        maxHealth: 3,
        currentHealth: 3,
        healthy: true,
        injured: false,
        unconscious: false
      },
      key: Date.now()
    }
  }

  submitPC = (event) => {
    event.preventDefault()
    this.props.addPC(this.state)
  }

  render() {
    return (
      <form>
        <button onClick={event => this.submitPC(event)} >üêô</button>
      </form>
    )
  }
}

export default Form
