import PC from './PC';
import './App.css';

class App extends Component {
  constructor() {
    super()
    this.state = {
      pcs: [],
      npcs: [],
      bads: []
    }
  }

  addPC = () => {
    const chef = {      
      charName: 'Chefolopod',
      playerName: 'Cat',
      concept: 'Food Bard',
      image: 'üêô',
    }

    this.setState({ pcs: chef})
  }

  render () {
    return (
      <main className="App">
        <nav>
          SideBar
        </nav>
        <section className="display">
          <p>PCs</p>
          <p>NPCs</p>
          <p>BADs</p>
        </section>
      </main>
    );
  }
}

export default App;
