import { eventWrapper } from "@testing-library/user-event/dist/utils";
import React, { Component } from "react";
import './Form.css'

class Form extends Component {
  constructor() {
    super()
    this.state = {
      charName: '',
      concept: 'Food Bard',
      playerName: 'Cat',
      image: 'üêô',
      attributes: {
        brawn: 4,
        agility: 3,
        intellegence: 1,
        wit: 3,
        charm: 1,
        presence: 3
      },
      vitals: {
        maxHealth: 3,
        currentHealth: 3,
        healthy: true,
        injured: false,
        unconscious: false
      },
    }
  }

  submitPC = (event) => {
    event.preventDefault()
    this.props.addPC(this.state)
  }

  render() {
    return (
      <form>
        <input type='text' name="playerName" placeholder="Player's Name" value={this.state.playerName}></input>
        <input type='text' name="charName" placeholder="Character's Name" value={this.state.charName}></input>

        <button onClick={event => this.submitPC(event)} >üêô</button>
      </form>
    )
  }
}

export default Form
