import React, {Component} from 'react'
import PCs from './PCs';
import Form from './Form';
import './App.css';

class App extends Component {
  constructor() {
    super()
    this.state = {
      pcs: [
        {
          charName: '',
          concept: '',
          playerName: '',
          image: '',
          attributes: {
            brawn: 1,
            agility: 1,
            intellegence: 1,
            wit: 1,
            charm: 1,
            presence: 1
          },
          vitals: {
            maxHealth: 3,
            currentHealth: 3,
            healthy: true,
            injured: false,
            unconscious: false
          }
        }
      ],
      npcs: [],
      bads: []
    }
  }

  addPC = (newPC) => {
    this.setState({ pcs: [...this.state.pcs, newPC]})
  }

  render () {
    return (
      <main className="App">
        <nav>
          <Form addPC={this.addPC} />
        </nav>
        <section className="display">
          <PCs pcs={this.state.pcs} />
          <p>NPCs</p>
          <p>BADs</p>
        </section>
      </main>
    );
  }
}

export default App;
