import React, { Component } from "react"
import PcCard from "./PcCard"
import './PCs.css'

const PCs = ({pcs}) => {

  const exampleChar = {
    charName: '',
    concept: '',
    playerName: '',
    image: '',
    brawn: 1,
    agility: 1,
    intellegence: 1,
    wit: 1,
    charm: 1,
    presence: 1,
    maxHealth: 0, //this.brawn * 3
    currentHealth: 0, //this.brawn * 3
    healthy: true,
    injured: false,
    unconscious: false,
    key: Date.now()
  }

  const displayCards = pcs.map(char => {
    return (
      <PcCard
        charName={char.charName}
        playerName={char.playerName}
        concept={char.concept} 
        image={char.image}
        attributes={char.attributes}
        vitals={char.vitals}
        key={Date.now()}
      />
    )
  })

  return (
    <section>
      {displayCards}
    </section>
  )
}

export default PCs
