import { eventWrapper } from "@testing-library/user-event/dist/utils";
import React, { Component } from "react";
import './Form.css'

class Form extends Component {
  constructor() {
    super()
    this.state = {
      charName: "Character's Name",
      concept: 'Character Concept',
      playerName: "Player's Name",
      image: 'üêô',
      attributes: {
        brawn: 4,
        agility: 3,
        intellegence: 1,
        wit: 3,
        charm: 1,
        presence: 3
      },
      vitals: {
        maxHealth: 3,
        currentHealth: 3,
        healthy: true,
        injured: false,
        unconscious: false
      },
    }
  }

  handleChange = (event) => {
    this.setState( {value: event.target.value})
  }

  submitPC = (event) => {
    event.preventDefault()
    this.props.addPC(this.state)
  }

  render() {
    return (
      <form>
        <input type='text' name="playerName" value={this.state.playerName} onChange={this.handleChange} ></input>
        <input type='text' name="charName" value={this.state.charName}></input>

        <button onClick={event => this.submitPC(event)} >üêô</button>
      </form>
    )
  }
}

export default Form
